import "reflect-metadata";

import { NetworkModel, MachineModel, MachinesModel, DiskModel, MachinesDeleteModel } from "../src/modules/models";
import { FilterOptions } from "../src/scheduler";
import { getClient } from "./client_loader";

async function main() {
    const grid3 = await getClient();

    // create network Object
    const n = new NetworkModel();
    n.name = "wedtest";
    n.ip_range = "10.249.0.0/16";

    // create disk Object
    const disk = new DiskModel();
    disk.name = "wedDisk";
    disk.size = 8;
    disk.mountpoint = "/testdisk";

    const server1_options: FilterOptions = {
        cru: 20,
        mru: 100, // GB
        country: "BE"
    };

    // create vm node Object
    const vm = new MachineModel();
    vm.name = "testvm";
    vm.node_id = +(await grid3.scheduler.filterNodes(server1_options))[0].nodeId; // TODO: allow random choise
    vm.disks = [disk];
    vm.public_ip = false;
    vm.planetary = true;
    vm.cpu = 1;
    vm.memory = 1024 * 2;
    vm.rootfs_size = 1;
    vm.flist = "https://hub.grid.tf/tf-official-apps/base:latest.flist";
    vm.entrypoint = "/sbin/zinit init";
    vm.env = {
        SSH_KEY:
            "ssh-rsa  ayoub@ayoub-Inspiron-3576",
    };

    // create VMs Object
    const vms = new MachinesModel();
    vms.name = "newVMS";
    vms.network = n;
    vms.machines = [vm];
    vms.metadata = "{'testVMs': true}";
    vms.description = "test deploying VMs via ts grid3 client";

    // deploy vms
    const res = await grid3.machines.deploy(vms);
    console.log(JSON.stringify(res));

    // get the deployment
    const l = await grid3.machines.getObj(vms.name);
    console.log(JSON.stringify(l));

    // // delete
    // const d = await grid3.machines.delete({ name: vms.name });
    // console.log(d);

    grid3.disconnect();
}

main();
